<?php/** * Template Name: Institutions List Page */get_header(); ?><div class="clear"></div></header> <!-- / END HOME SECTION  --><div id="content" class="site-content">	<div class="container">		<div class="content-left-wrap col-md-12">			<div id="primary" class="content-area">				<main id="main" class="site-main" role="main">					<div class="row select-section">						<div class="col-lg-4 pull-right">							<form id="search" class="navbar-form" role="search">								<div class="input-group">									<input type="text" class="form-control" placeholder="Search Institution" value="<?php echo isset($_GET['q']); ?>" name="q">									<div class="input-group-btn">										<button class="btn btn-default" type="submit"><i class="fa fa-search"></i></button>									</div>								</div>							</form>						</div>					</div>					<?php					//Get all online post					$posts = get_posts(array(						'numberposts'	=> -1,						'post_type'		=> 'institution'					));					if( $posts ): ?>						<ul class="list list-unstyled">						<?php foreach( $posts as $post): // variable must be called $post (IMPORTANT) ?>							<?php setup_postdata($post); ?>							<li class="col-md-4">    							<div class="panel panel-default panel-primary grid-holder">        							<div class="panel-heading">    								    <a href="<?php the_permalink(); ?>"><i class="fa fa-graduation-cap"></i> <?php the_title(); ?>    								</div>    								<div class="panel-body">    								    <div class="inst-img">        								    <img class="img img-responsive" src="<?php the_field('institution_picture'); ?>" /></a>        								</div>        								<div class="inst-desc">        									<?php        										$text = get_field('institution_description');        										echo wp_trim_words( $text, 20);        									?>        								</div>        							</div>                                </div>							</li>						<?php endforeach; ?>						</ul>						<?php wp_reset_postdata(); // IMPORTANT - reset the $post object so the rest of the page works correctly ?>					<?php endif; ?>				</main><!-- #main -->			</div><!-- #primary -->		</div><!-- .content-left-wrap -->	</div><!-- .container --><?php get_footer(); ?>